for x in range(1,10): #<--------- Enquanto " x " estiver entre "1" e "10", escreva o número correspondente. E exclua o último número, nesse caso o "10".
    print(x) #<------------------ Escreva o valor de "x".

print('-'*30) #<----------------- Escreve o símbolo de menos " - " 30 vezes. Foi usado para criar uma separação entre os testes.
